let ContactSensor = await $.get("Z2M.ContactSensor");
let EnvironmentSensor = await $.get("Z2M.EnvironmentSensor");
let WaterLeakSensor = await $.get("Z2M.WaterLeakSensor");

let Button = await $.get("Z2M.Button");

