let DoubleSwitch = await $.get("Z2M.DoubleSwitch");
let Light = await $.get("Z2M.Light");
let Blind = await $.get("Z2M.Blind");

class Z2M {
    static MotionSensor = MotionSensor;
    static ContactSensor = ContactSensor;
    static EnvironmentSensor = EnvironmentSensor;
    static WaterLeakSensor = WaterLeakSensor;

    static Button = Button;

    static Switch = Switch;
    static DoubleSwitch = DoubleSwitch;
    static Light = Light;
    static Blind = Blind;
}

$.register("Z2M.*", Z2M);

