let $ = global.get("registry");
let Plugin = await $.get("Plugin");
let Property = await $.get("Property");
let MqttPlugin = await $.get("Mqtt.Plugin");

let Z2M = await $.get("Z2M.*");
let Hass = await $.get("Hass.*");

class Z2mMqttPlugin extends MqttPlugin {
    subscribe(args) {
        if (!args.properties) args.properties = {};

        if (args.group instanceof Z2M.Button) {
            args.properties.Action = Object.assign({ name: "action" }, args.properties.Action);
        }

        if (args.group instanceof Z2M.MotionSensor) {
            args.properties.Occupancy = Object.assign({ name: "occupancy" }, args.properties.Occupancy);
        }

        if (args.group instanceof Z2M.ContactSensor) {
            args.properties.Contact = Object.assign({ name: "contact" }, args.properties.Contact);
        }

        if (args.group instanceof Z2M.WaterLeakSensor) {
            args.properties.WaterLeak = Object.assign({ name: "water_leak" }, args.properties.WaterLeak);
        }

        if (args.group instanceof Z2M.EnvironmentSensor) {
            args.properties.Temperature = Object.assign({ name: "temperature" }, args.properties.Temperature);
            args.properties.Humidity = Object.assign({ name: "humidity" }, args.properties.Humidity);
            args.properties.Pressure = Object.assign({ name: "pressure" }, args.properties.Pressure);
        }

        if (args.group instanceof Z2M.Switch) {
            args.properties.State = Object.assign({ name: "state", outgoingConverter: state => state? 'on' : 'off' }, args.properties.State);
            args.properties.Power = Object.assign({ name: "power" }, args.properties.Power);
            args.properties.Energy = Object.assign({ name: "energy" }, args.properties.Energy);
        }

        if (args.group instanceof Z2M.DoubleSwitch) {
            args.properties.Left = Object.assign({ name: "state_left", outgoingConverter: state => state ? 'on' : 'off' }, args.properties.Left);
            args.properties.Right = Object.assign({ name: "state_right", outgoingConverter: state => state ? 'on' : 'off' }, args.properties.Right);
            args.properties.Power = Object.assign({ name: "power" }, args.properties.Power);
            args.properties.Energy = Object.assign({ name: "energy" }, args.properties.Energy);
        }

        if (args.group instanceof Z2M.Light) {
            args.properties.State = Object.assign({ name: "state", outgoingConverter: state => state ? 'on' : 'off' }, args.properties.State);
            args.properties.Brightness = Object.assign({ name: "brightness", incomingConverter: data => Math.floor(data / 255 * 100), outgoingConverter: data => Math.floor(data / 100 * 255) }, args.properties.Brightness);
            args.properties.Kelvin = Object.assign({ name: "kelvin" }, args.properties.Kelvin);
        }

        if (args.group instanceof Z2M.Blind) {
            args.properties.Position = Object.assign({ name: "position", incomingConverter: value => 100 - value, outgoingConverter: value => 100 - value }, args.properties.State);
        }

        super.subscribe(args);
    }
}

$.register("Z2M.MqttPlugin", Z2mMqttPlugin);

return msg;

let $ = global.get("registry");
let Plugin = await $.get("Plugin");
let Property = await $.get("Property");
let HassPlugin = await $.get("Hass.Plugin");
let Z2MPlugin = await $.get("Z2M.MqttPlugin");

let Z2M = await $.get("Z2M.*");

class Z2MHass extends HassPlugin {
    mqtt;

    init(args) {
        super.init(args);
        this.mqtt = new Z2MPlugin();
        this.mqtt.init({target: this.target, mqttService: this.mqttService});
    }

    z2mlight(propertyGroup, name = "Light") {
        this.publish({
            name: name,
            topic: `${this.home}/${this.room}/${name}`,
            group: propertyGroup,
        });
    }


    blind(propertyGroup, name = "Blind") {
        this.publish({
            name: name,
            topic: `${this.home}/${this.room}/${name}`,
            group: propertyGroup,
            properties: {
                device_class: "blind"
            }
        });
    }

    environment(propertyGroup, name = "EnvironmentSensor") {
        this.publish({
            name: name,
            topic: `${this.home}/${this.room}/${name}`,
            group: propertyGroup,
            properties: {
                Temperature: { unit: "°C", device_class: "temperature" },
                Humidity: { unit: "%", device_class: "humidity" },
                Pressure: { unit: "Pa", device_class: "pressure" },
            }
        });
    }

    singleSwitch(propertyGroup, name = "Switch") {
        this.publish({
            name: name,
            topic: `${this.home}/${this.room}/${name}`,
            group: propertyGroup,
            properties: {
                State: {},
                Power: { unit: "W", device_class: "power" },
                Energy: { unit: "kWh", state_class: "total_increasing" ,device_class: "energy" },
            }
        });
    }

    motion(propertyGroup, name = "Motion") {
        this.publish({
            name: name,
            topic: `${this.home}/${this.room}/${name}`,
            group: propertyGroup,
            properties: {
                Occupancy: {device_class: "motion"},
            }
        });
    }

    contact(propertyGroup, name = "Contact") {
        this.publish({
            name: name,
            topic: `${this.home}/${this.room}/${name}`,
            group: propertyGroup,
            properties: {
                Contact: { device_class: "door", payload_on: false, payload_off: true },
            }
        });
    }

    waterleak(propertyGroup, name = "WaterLeak") {
        this.publish({
            name: name,
            topic: `${this.home}/${this.room}/${name}`,
            group: propertyGroup,
            properties: {
                WaterLeak: { device_class: "problem" },
            }
        });
    }
}

$.register("Z2M.HassPlugin", Z2MHass);

return msg;

