let $ = global.get("registry");
let Property = await $.get("Property");
let Hass = await $.get("Hass.*");

class Switch extends Property.Group {
    constructor() {
        super({
            "State": new Hass.Switch(),
            "Energy": new Hass.Sensor(),
            "Power": new Hass.Sensor(),
        });
    }

    changed() {
        if (this.State.value)
            this.green(`${this.Energy.value} kWh (${0.0 + this.Power.value} W)`);
        else
            this.red(`${this.Energy.value} kWh`);
    }
}

$.register("Z2M.Switch", Switch);

return msg;

