class WaterLeakSensor extends Property.Group {
    constructor() {
        super({
            "WaterLeak": new Hass.BinarySensor()
        });
    }

    changed() {
        if (this.WaterLeak.value)
            this.red();
        else
            this.green();
    }
}

$.register("Z2M.WaterLeakSensor", WaterLeakSensor);

return msg;

let $ = global.get("registry");
let Property = await $.get("Property");

let Hass = await $.get("Hass.*");

class Blind extends Hass.Cover {
    constructor() {
        super();
        this.Position.on("changed", data => {
            if (data.to === 100) this.State.set("closed");
            else if(data.to === 0) this.State.set("open");
            else if(data.to > data.from) this.State.set("closing");
            else if(data.to < data.from) this.State.set("opening");
        })
        this.State.on("command", value => {
            if (value === "open") this.Position.command(0);
            if (value === "close") this.Position.command(100);
            if (value === "stop") this.Position.command(this.Position.value);
        })
    }
}

$.register("Z2M.Blind", Blind);

return msg;

